mapper.update:
update f_chat_list
   set user_no = #{userNo}
 where chat_no = (select chat_no 
                   from( 
                        select * from f_chat_list
                         where user_no is null
                         order by DBMS_RANDOM.RANDOM
                       ) 
                   where rownum <2
                  )

update f_consulting
   set user_no = #{userNo}
 where counselor_no = #{counselorNo}

mapper.select:
select counselor_no, user_no
  from f_consulting
 where counselor_no = #{checkNo} or user_no = #{checkNo}
